DROP TABLE IF EXISTS tbl_news_articles;

CREATE TABLE tbl_news_articles (
  id BIGINT(19) AUTO_INCREMENT PRIMARY KEY,
  header VARCHAR(256) NOT NULL,
  short_desc VARCHAR(256) NOT NULL,
  text CLOB NOT NULL,
  publish_date BIGINT NOT NULL,
  author VARCHAR(256) NOT NULL,
  keyword VARCHAR(256) NOT NULL
);

